                                    DechargerVM.CurrentFileType = clsFileType.MSFileType.Thermo;

                                    App.CurrentFileType = clsFileType.MSFileType.Thermo;

                                    DechargerVM.CurrentFilesLoaded.Add(aFile.Name);
