        private void InitCharts(IMSProvider x)
        {
            try
            {
                //System.Diagnostics.Debug.WriteLine("Time to bind: " + DateTime.Now.Subtract(start).TotalMilliseconds + " ms");
                //

                //System.Diagnostics.Debug.WriteLine("Time to bind: " + DateTime.Now.Subtract(start).TotalMilliseconds + " ms");
