            {
                e.Handled = true;

                var tb = ((TextBox)sender);
                var cursorLocation = tb.CaretIndex;
                tb.Text = tb.Text.Insert(cursorLocation, "\t");
                tb.Select(cursorLocation + 1, 0);
                //tb.Text += "\t";
                //tb.Select(tb.Text.Length, 0);
            }
