        private Thickness MarkerThickness(double th)
        {
            Thickness thick = new Thickness();
            if (th <= 1400)
            {
                thick.Left = 1;
                thick.Bottom = 1;
                return thick;
            }
            else if (th >= 1401 && th <= 2600)
            {
                thick.Left = 3;
                thick.Bottom = 3;
                return thick;
            }
            else if (th >= 2601 && th <= 3800)
            {
                thick.Left = 6;
                thick.Bottom = 6;
                return thick;
            }
            else if (th >= 3801)
            {
                thick.Left = 10;
                thick.Bottom = 10;
                return thick;
            }
            return thick;
        }

