        private void DisplayBYCZErrors()
        {
            Dispatcher.Invoke((Action)(() =>
            {
                if (Properties.Settings.Default.PPMErrorPlot)
                {
                    Properties.Settings.Default.EnabledCZErrors = true;
                    Properties.Settings.Default.EnabledBYErrors = true;
                }
